mapper.select:
	    SELECT * FROM fund_managers

	    	 and id=#{id} 
	    	 and ename like concat(concat('%',#{ename}),'%') 
   	    	 and cname like concat(concat('%',#{cname}),'%') 

	    		and ctype in  #{ct}

mapper.update:
UPDATE fund_managers
ename=#{ename}, cname=#{cname}, ctype=#{ctype}, itime=#{itime}, otime=#{otime}, resume=#{resume},
WHERE id=#{id}

mapper.select:
	    SELECT fi.*, fm.cname uname FROM fund_info fi left join fund_managers fm on fm.id=fi.userid

	    	 and fi.id=#{id} 
	    	 and fi.userid=#{userid} 
	    	 and fi.fcode like concat(concat('%',#{fcode}),'%') 
   	    	 and fi.fname like concat(concat('%',#{fname}),'%') 
   	    	 and fm.cname like concat(concat('%',#{uname}),'%') 

    			and fi.id in  #{ct}

mapper.update:
		UPDATE fund_info

				 fcode=#{fcode}, 
   	    		 fname=#{fname}, 
   	    		 userid=#{userid}, 


				 and id=#{id} 

	    			and id in  #{ct}
